/*
    On veut récupérer les coordonnées GPS des noeuds du trajet pour cela on utilise 
    l'API OpenStreetMap qui renvoie les information des noeuds.
    On prépare aussi la donnée reçue par l'API precédente pour la conserver dans 
    nodes et duration.
*/
trajet = msg.payload.routes[0];
nodes = [];
durations = [];

let request = "https://api.openstreetmap.org/api/0.6/nodes?nodes=";

for( i = 0; i < trajet.legs.length; i++ ){
    for( j = 0; j < trajet.legs[i].annotation.nodes.length; j++ ){
        let n = trajet.legs[i].annotation.nodes[j];
        request = request + n + ",";
        nodes.push( n )
    }    
    for( k = 0; k < trajet.legs[i].annotation.duration.length; k++ ){
        let d = trajet.legs[i].annotation.duration[k];
        durations.push( d )
    }
}

request = request.slice(0, -1);
msg.payload = request;

msg.nodes = nodes.reduce(function(a,b){if(a.indexOf(b)<0)a.push(b);return a;},[]);;
msg.durations = durations;
return msg;

