const seuilDistance = 20 // distance en mètre en dessous de laquel on considére que le bus est a l'arret

function degreesToRadians(degrees) {
  return degrees * Math.PI / 180;
}

function distanceInKmBetweenEarthCoordinates(lat1, lon1, lat2, lon2) {
  var earthRadiusKm = 6371;

  var dLat = degreesToRadians(lat2-lat1);
  var dLon = degreesToRadians(lon2-lon1);

  lat1 = degreesToRadians(lat1);
  lat2 = degreesToRadians(lat2);

  var a = Math.sin(dLat/2) * Math.sin(dLat/2) +
          Math.sin(dLon/2) * Math.sin(dLon/2) * Math.cos(lat1) * Math.cos(lat2); 
  var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
  return earthRadiusKm * c;
}

let idLigne = msg.payload.idLigne
let lignes = flow.get("Lignes");
let ligne={};
let n;
let arrets = flow.get("Arrets");
let newMsg = null;
let newMsg2 = {};
let newMsg3 = {};
let date = new Date();

// Selectionner la ligne sur laquel le bus est
for ( i = 0; i < lignes.length; i++ ){
    if ( lignes[i]._id == idLigne){
      ligne = lignes[i];
      n = i;
      i = lignes.length;
    }
}

let lastRegistredStopId = null
let lastRegistredStop =  lignes[n].bus_passage_at_stop[ (lignes[n].bus_passage_at_stop.length) - 1 ]
if ( lastRegistredStop !== undefined){
    lastRegistredStopId = lastRegistredStop.idArret;
}

// parcourir tous les arrets de bus pour savoir s'il y en a un a moins de 5m
for (i = 0; i < ligne.bus_route.length; i++ ){
    idBusStop = ligne.bus_route[i];// l'arrêt de bus dont on veux les infos

    // Parcourrir les arret pour trouver les infos de cet arrêt
    for (j = 0; j < arrets.length; j++ ){
        let arret = arrets[j]; // l'arrêt de bus evalué a ce tour de boucle

        // Si on a trouvé l'arret de même id
        if ( arret._id == idBusStop){

            // on évalue sa distance au bus
            d = distanceInKmBetweenEarthCoordinates(arret.coordonnees[0], arret.coordonnees[1], msg.payload.lat, msg.payload.lon);
            d = d * 1000 //conversion en m
            if ( (d < seuilDistance) && (lastRegistredStopId !== arret._id) ){

                //if ( lastRegistredStopId !== arret._id){

                    newMsg = {};
                    newMsg.query= {"name": ligne.name };
                    newMsg.payload = { "$push": {"bus_passage_at_stop": {"date": date, "idArret" :arret._id }} };

                    lignes[n].bus_passage_at_stop.push({"date": date, "idArret" :arret._id })
                //}
            }
        }
    }
}

let timeSinceLastStop  = 0
// time in secondes/ substraction of two dates returns time in Milliseconds
if (lastRegistredStop !== undefined ){
    timeSinceLastStop  =  (date - lastRegistredStop.date) / 1000;
} 


// pour quand un bus n'as pas voyagé depuis plus d'une heure, là pour empecher les bug pendant les tests
if ( timeSinceLastStop >  60 * 60){
    timeSinceLastStop = 0;
}

// calcul du temps pour aller au prochain arret sur cette ligne
let timeSum = 0;
let nbFoisTrajetLastToNext = 0;
// On parcourt les arrets par lequel les bus sont passé sur la ligne
for ( i = 0; i<  ligne.bus_passage_at_stop.length - 1 ; i++){
    // si on trouve un passage a un arret identique au précedent 
    if( ligne.bus_passage_at_stop[i].idArret == lastRegistredStopId  ){
        nbFoisTrajetLastToNext ++;
        j = i+1;
        timeSum = timeSum + ( ligne.bus_passage_at_stop[j].date - ligne.bus_passage_at_stop[i].date )
    }
}
// timeSum en secondes
timeSum = timeSum/1000;

let tempsMoyenEntreArret = 0;
let tempsAvantLeProchainArret = 0;


// le temps moyens du trajet moins le temps de trajet déja effectué
if (nbFoisTrajetLastToNext !== 0 ){
    tempsMoyenEntreArret= timeSum / nbFoisTrajetLastToNext;
    tempsAvantLeProchainArret = tempsMoyenEntreArret - timeSinceLastStop;
}

newMsg2.payload ={
